// Serper returns JSON with "organic" results array
const results = items[0].json.organic || [];
const leads = [];

for (const r of results) {
  if (!r.link) continue;

  leads.push({
    json: {
      company_name: (r.title || "").replace(/\|.*$/, "").trim(),
      website: r.link,
      snippet: r.snippet || "",
      status: "raw_from_serper"
    }
  });
}

return leads;

// Get all the leads from the previous node
const leads = $input.all().map((item) => item.json);

// This is our new, smarter blocklist. It looks for patterns, not just names.
const junkKeywords = [
  'top 10', 'top 20', 'best agencies', 'best companies', 'top rated',
  'reviews of', 'list of', 'directory of', 'find the best', 'agencies in',
  'firms in', 'jobs in', 'has anyone', 'reddit.com', 'youtube.com',
  'indeed.com', 'glassdoor.com', 'clutch.co', 'designrush.com',
  'goodfirms.co', 'themanifest.com'
];

const normalizedLeads = leads
  .map((lead) => {
    // Clean up the company name and website URL
    let cleanCompanyName = lead.company_name
      .split(' - ')[0]
      .split(' ‚Ä¢ ')[0]
      .split(' (@')[0]
      .trim();

    let cleanWebsite = lead.website.trim();
    if (cleanWebsite.endsWith('/')) {
      cleanWebsite = cleanWebsite.slice(0, -1);
    }

    return {
      company_name: cleanCompanyName,
      website: cleanWebsite,
      original_title: lead.title, // Keep the original title for checking
      original_snippet: lead.snippet, // Keep the original snippet for checking
      status: "normalized"
    };
  })
  .filter((lead) => {
    // Now, check if the original title or website contains any junk keywords
    const textToSearch = (lead.original_title + ' ' + lead.website).toLowerCase();

    const isJunk = junkKeywords.some(keyword => textToSearch.includes(keyword));

    // Return true only if it's NOT junk
    return !isJunk;
  });

return normalizedLeads;

// This node receives all the separate findings from the deep scan
const allItems = $input.all();
const aggregatedResult = {
  emails: [],
  phones: [],
  contact_form_present: false,
  summaries: []
};

// Loop through each finding (from the about page, contact page, etc.)
for (const item of allItems) {
  // 1. Get the raw string from the 'output' field.
  const rawOutput = item.json.output;
  if (!rawOutput) continue; // Skip if the AI returned nothing.

  try {
    // 2. Clean the string to remove markdown and get the pure JSON.
    const jsonString = rawOutput.replace(/```json\n|\n```/g, '').trim();
    if (!jsonString) continue; // Skip if the string is empty after cleaning.

    // 3. Parse the string into a usable object. THIS IS THE STEP WE MISSED.
    const data = JSON.parse(jsonString);

    // 4. Now, the original aggregation logic will work on the clean data.
    if (data.emails && data.emails.length > 0) {
      aggregatedResult.emails.push(...data.emails);
    }
    if (data.phones && data.phones.length > 0) {
      aggregatedResult.phones.push(...data.phones);
    }
    if (data.contact_form_present) {
      aggregatedResult.contact_form_present = true;
    }
    if (data.summary) {
      aggregatedResult.summaries.push(data.summary);
    }
  } catch (error) {
    // This will prevent the workflow from crashing if the AI returns bad JSON.
    console.error("Could not parse AI output:", item.json.output, error);
  }
}

// IMPORTANT: Remove any duplicate emails or phone numbers
aggregatedResult.emails = [...new Set(aggregatedResult.emails)];
aggregatedResult.phones = [...new Set(aggregatedResult.phones)];

// Return a single, clean object with all the combined results
return [{ json: aggregatedResult }];

